import pygame, numpy, sys
import pygame.gfxdraw

threshold = 0

def valToCol(val):
    if abs(val) < threshold:
        return (0, 0, 0)
    
    sgm = 2.0/(1+numpy.exp(val))-1

    if sgm < 0:
        color = (-sgm*255, 0, 0)
    else:
        color = (0, sgm*255, 0)
    
    return color

